TtForm.FormLogic:
AddLoadCallback(fn_FormLoadSOOShellFormatting);

function fn_FormLoadSOOShellFormatting(e)
{
  action_SOOLoadUtils();

  ActivateTab("detailsTab");
}


AddLoadCallback(fn_RemoveBorders);

function fn_RemoveBorders(e)
{
  action_SOORemoveExpanderBorders("Attachments_Section");

  action_SOORemoveExpanderBorders("ChangeHistory_Section");

  action_SOORemoveExpanderBorders("Notes_Section");

  action_SOORemoveExpanderBorders("ActiveSubtasksExpander");

  action_SOORemoveExpanderBorders("StateChangeHistory_Section");
}


function action_SOOLoadUtils()
{
  SOO.ShellFormatting.init();
}

function action_SOORemoveExpanderBorders(source)
{
  SOO.ShellFormatting.stylizeExpander(source);
}
