TtForm.FormLogic:
AddLoadCallback(fn_FormLoadSOOShellFormatting);

function fn_FormLoadSOOShellFormatting(e)
{
  action_SOOLoadUtils();

  ActivateTab("detailsTab");
}


AddLoadCallback(fn_OnLoadIfDescIsEmptyHideTopLine);

function fn_OnLoadIfDescIsEmptyHideTopLine(e)
{
  if (condition_FieldOrInputControlEmptyOperator("DESCRIPTION", "is empty"))
  {
    SetVisible("TopLinePanel", "hide");

    SetVisible("DESCRIPTION", "hide");
  }
}


function condition_FieldOrInputControlEmptyOperator(source, operator)
{
  if (operator == "is empty") return IsFieldEmpty(source);
  else return !IsFieldEmpty(source);
}

function action_SOOLoadUtils()
{
  SOO.ShellFormatting.init();
}
