TtForm.FormLogic:
AddLoadCallback(fn_FormLoadConfigureUI);

function fn_FormLoadConfigureUI(e)
{
  action_SOOLoadUtils();

  action_SOOSetupMoreActions("MoreActions");

  ActivateTab("DetailsTab");

  SetVisible("HiddenFieldsPanel", "hide");
}


AddLoadCallback(fn_FormLoadInitializeFormStack);

function fn_FormLoadInitializeFormStack(e)
{
  SBMSOL.FormUtil.reportNavigation();
$("#toggle .separator").remove();
  $("#goback").prependTo("#iterate");
$('#toggle').show();
//jQuerySBM(document.getElementById("backToResultsContainer")).insertBefore('#reportToggle');
;
}


AddLoadCallback(fn_FormLoadHideChildTasksIfNoLinkedChildren);

function fn_FormLoadHideChildTasksIfNoLinkedChildren(e)
{
  if (condition_FieldOrInputControlEmptyOperator("MREL_TASKS", "is empty"))
  {
    SetVisible("CRTasksTab", "hide");
  }
}


AddLoadCallback(fn_FormLoadHideUnnecessaryTabsBasedOnLinkedCP);

function fn_FormLoadHideUnnecessaryTabsBasedOnLinkedCP(e)
{
  if (condition_FieldOrInputControlEmptyOperator("SREL_EPIC_STORY", "is not empty"))
  {
    SetVisible("ChangeRequestsTab", "hide");
  }
  else
  {
    SetVisible("ChangeRequestsTab", "show");
  }
}


function condition_FieldOrInputControlEmptyOperator(source, operator)
{
  if (operator == "is empty") return IsFieldEmpty(source);
  else return !IsFieldEmpty(source);
}

function action_SOOLoadUtils()
{
  SOO.ShellFormatting.init();
}

function action_SOOSetupMoreActions(source)
{
  //SBMSOL.FormUtil.MoreActions.setupMoreActions(source);

          SOO.ShellFormatting.MoreActions.setupMoreActions(source);
}
