TtForm.FormLogic:
AddLoadCallback(fn_FormAction);

function fn_FormAction(e)
{
  action_SOOLoadUtils();

  SetVisible("HiddenFields", "hide");

  ActivateTab("DetailsTab");
}


AddChangeCallback("RELATED_TRACKING_SYSTEM", fn_FormAction8);
AddLoadCallback(fn_FormAction8);

function fn_FormAction8(e)
{
  if (condition_FieldOrControlOperatorValue("RELATED_TRACKING_SYSTEM", "in", [ $SELECTIONVALUEID(087b5574-b3ac-40c2-a1ff-6b54ef580ac9) ]) || condition_FieldOrControlOperatorValue("RELATED_TRACKING_SYSTEM", "in", [ 0 ]))
  {
    SetVisible("RELATED_TRACKING_NUMBER", "hide");

    FoC_SetRequired("RELATED_TRACKING_NUMBER", "optional");
  }
  else
  {
    SetVisible("RELATED_TRACKING_NUMBER", "show");

    FoC_SetRequired("RELATED_TRACKING_NUMBER", "required");
  }
}


AddLoadCallback(fn_FormAction6);

function fn_FormAction6(e)
{
  if (condition_FieldOrControlVisible("COMMENTS", "hidden"))
  {
    SetVisible("CommentsPanel", "hide");
  }
}


AddLoadCallback(fn_FormAction7);

function fn_FormAction7(e)
{
  if (condition_FieldOrControlVisible("Attachments_Section", "hidden"))
  {
    SetVisible("AttachmentsPanel", "hide");
  }
}


function condition_FieldOrControlOperatorValue(source, operator, value)
{
  return evaluateFieldOperatorValue(source, operator, value);
}

function condition_FieldOrControlVisible(source, value)
{
  return (value == "visible") ? IsShown(source) : !IsShown(source);
}

function action_SOOLoadUtils()
{
  SOO.ShellFormatting.init();
}
