TtForm.FormLogic:
AddLoadCallback(fn_FormLoadSSMFormatting);

function fn_FormLoadSSMFormatting(e)
{
  action_SOOLoadUtils();

  SetVisible("HiddenFieldsPanel", "hide");

  SetVisible("WorkLogPanel", "hide");

  SetVisible("CommentsPanel", "hide");

  SetVisible("RolesPanel", "hide");
}


AddLoadCallback(fn_FormAction4);

function fn_FormAction4(e)
{
  if (condition_CurrentTransition("in", "CRS_CHANGE_REQUESTS.ADD_WORK_LOG"))
  {
    SetVisible("CommentsPanel", "show");

    SetBackgroundColor("CommentsPanel", "rgb(255, 253, 215)");
  }
  else
  {
    SetVisible("CommentsPanel", "hide");
  }
}


function condition_CurrentTransition(operator, value)
{
  var asInternalNames = value.split(',');
  var transitionInternalName = GetTransitionInternalName();
  for (var i = 0; i < asInternalNames.length; i++)
  {
    if (transitionInternalName == asInternalNames[i]) 
    {
      return (operator == "in");
    }
  }
  return (operator != "in");
}

function action_SOOLoadUtils()
{
  SOO.ShellFormatting.init();
}
