TtForm.FormLogic:
AddLoadCallback(fn_FormLoadFormatForm);
AddPageValidationFailedCallback(fn_FormLoadFormatForm);

function fn_FormLoadFormatForm(e)
{
  action_SOOLoadUtils();

  var CustomErrorMsg = $('#CustomErrorMsg').detach();
 CustomErrorMsg.insertBefore($('#' + LookupFieldId("DetailsTab")));
var errMsg = $('#ErrorMsg').detach();
 errMsg.InsertBefore($('#' + LookupFieldId("DetailsTab")));
}


AddLoadCallback(fn_FormLoadActivateRequirementsTab);

function fn_FormLoadActivateRequirementsTab(e)
{
  ActivateTab("DetailsTab");
}


AddChangeCallback("RELATED_TRACKING_SYSTEM", fn_FormAction);
AddLoadCallback(fn_FormAction);

function fn_FormAction(e)
{
  if (condition_FieldOrControlOperatorValue("RELATED_TRACKING_SYSTEM", "in", [ $SELECTIONVALUEID(087b5574-b3ac-40c2-a1ff-6b54ef580ac9) ]) || condition_FieldOrControlOperatorValue("RELATED_TRACKING_SYSTEM", "in", [ 0 ]))
  {
    SetVisible("RELATED_TRACKING_NUMBER", "hide");

    FoC_SetRequired("RELATED_TRACKING_NUMBER", "optional");
  }
  else
  {
    SetVisible("RELATED_TRACKING_NUMBER", "show");

    FoC_SetRequired("RELATED_TRACKING_NUMBER", "required");
  }
}


AddLoadCallback(fn_FormLoadHideHiddenFields);

function fn_FormLoadHideHiddenFields(e)
{
  SetVisible("HiddenFields", "hide");
}


AddSubmitCallback(fn_FormSubmitSubmitOnBehalf2);

function fn_FormSubmitSubmitOnBehalf2(e)
{
  if (condition_FieldOrControlOperatorValue("SUBMIT_ON_BEHALF_ENABLED", "=", "0") && condition_Javascript1("True"))
  {
    performSetFieldValue("SELF_SERVICE_REQUEST", "1", true);

    //Set Submitter Field >>
var affectedUser = jQuerySBM("option:selected", GetFieldByName("ASSIGNEE")).text();
SetFieldValue("Submitter", affectedUser);
  }
  else if (condition_FieldOrControlOperatorValue("SUBMIT_ON_BEHALF_ENABLED", "=", "1") && condition_Javascript2("True"))
  {
    performSetFieldValue("SELF_SERVICE_REQUEST", "1", true);
  }
  else
  {
    performSetFieldValue("SELF_SERVICE_REQUEST", "0", true);
  }
}


