TtForm.FormLogic:
AddLoadCallback(fn_FormLoadHideForm);

function fn_FormLoadHideForm(e)
{
  jQuerySBM('#SectionWrapper').addClass('visuallyhidden');
}


AddLoadCallback(fn_FormAction);

function fn_FormAction(e)
{
  ActivateTab("DetailsTab");

  SetVisible("HiddenFieldsPanel", "hide");
}


AddLoadCallback(fn_FormLoadResizeSections);

function fn_FormLoadResizeSections(e)
{
  FoC_SetHeightToFill("RightColumnPanel");
}


AddLoadCallback(fn_FormAction4);

function fn_FormAction4(e)
{
  if (condition_CurrentTransition("in", "CRS_CHANGE_REQUESTS.SUBMIT"))
  {
    performSetFieldValue("titleText", "<b>Submit into:</b> $SINGULARPROJECTNAME()", true);
  }
}


AddLoadCallback(fn_FormLoadShowForm);

function fn_FormLoadShowForm(e)
{
  jQuerySBM('#SectionWrapper').removeClass('visuallyhidden');
}


function condition_CurrentTransition(operator, value)
{
  var asInternalNames = value.split(',');
  var transitionInternalName = GetTransitionInternalName();
  for (var i = 0; i < asInternalNames.length; i++)
  {
    if (transitionInternalName == asInternalNames[i]) 
    {
      return (operator == "in");
    }
  }
  return (operator != "in");
}
